package week1.day1;

public class LearnDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte age=40;
		short year=2024;
		int salary=500000;
		long mobile=916230845623L;
		
		System.out.println("byte dateType --- Age is "+age);
		System.out.println("short dateType --- year is "+year);
		System.out.println("int dateType --- salary is "+salary);
		System.out.println("long dateType --- mobile is "+mobile);
		
		double pieValue=3.133333333333333333344; //it ll display only 15 digits
		System.out.println("double datatype ---- pievalue is "+pieValue);
		
		float roi=10.15F;
		System.out.println("float datatype ---- roi is"+ roi);
		
		boolean hadBreakfast= false;
		
		char initial='N';
		char asciiValue=65;
		System.out.println("char datatype ---- initial is"+ initial);
		System.out.println("char datatype ---- asciiValue is"+ asciiValue);
		
		String name="Nisha";
		System.out.println("String datatype ---- name is"+ name);
		
	}

}
